describe ScreenShotsController do

  # This should return the minimal set of attributes required to create a valid
  # ScreenShot. As you add validations to ScreenShot, be sure to
  # update the return value of this method accordingly.
  def valid_attributes
    {}
  end

  describe "GET index" do
    it "assigns all screen_shots as @screen_shots" do
      screen_shot = ScreenShot.create! valid_attributes
      get :index
      assigns(:screen_shots).should eq([screen_shot])
    end
  end

  describe "GET show" do
    it "assigns the requested screen_shot as @screen_shot" do
      screen_shot = ScreenShot.create! valid_attributes
      get :show, :id => screen_shot.id.to_s
      assigns(:screen_shot).should eq(screen_shot)
    end
  end

  describe "GET new" do
    it "assigns a new screen_shot as @screen_shot" do
      get :new
      assigns(:screen_shot).should be_a_new(ScreenShot)
    end
  end

  describe "GET edit" do
    it "assigns the requested screen_shot as @screen_shot" do
      screen_shot = ScreenShot.create! valid_attributes
      get :edit, :id => screen_shot.id.to_s
      assigns(:screen_shot).should eq(screen_shot)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "creates a new ScreenShot" do
        expect {
          post :create, :screen_shot => valid_attributes
        }.to change(ScreenShot, :count).by(1)
      end

      it "assigns a newly created screen_shot as @screen_shot" do
        post :create, :screen_shot => valid_attributes
        assigns(:screen_shot).should be_a(ScreenShot)
        assigns(:screen_shot).should be_persisted
      end

      it "redirects to the created screen_shot" do
        post :create, :screen_shot => valid_attributes
        response.should redirect_to(ScreenShot.last)
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved screen_shot as @screen_shot" do
        # Trigger the behavior that occurs when invalid params are submitted
        ScreenShot.any_instance.stub(:save).and_return(false)
        post :create, :screen_shot => {}
        assigns(:screen_shot).should be_a_new(ScreenShot)
      end

      it "re-renders the 'new' template" do
        # Trigger the behavior that occurs when invalid params are submitted
        ScreenShot.any_instance.stub(:save).and_return(false)
        post :create, :screen_shot => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested screen_shot" do
        screen_shot = ScreenShot.create! valid_attributes
        # Assuming there are no other screen_shots in the database, this
        # specifies that the ScreenShot created on the previous line
