        ScreenShot.any_instance.should_receive(:update_attributes).with({'these' => 'params'})
        put :update, :id => screen_shot.id, :screen_shot => {'these' => 'params'}
      end

      it "assigns the requested screen_shot as @screen_shot" do
        screen_shot = ScreenShot.create! valid_attributes
        put :update, :id => screen_shot.id, :screen_shot => valid_attributes
        assigns(:screen_shot).should eq(screen_shot)
      end

      it "redirects to the screen_shot" do
        screen_shot = ScreenShot.create! valid_attributes
        put :update, :id => screen_shot.id, :screen_shot => valid_attributes
        response.should redirect_to(screen_shot)
      end
    end

    describe "with invalid params" do
      it "assigns the screen_shot as @screen_shot" do
        screen_shot = ScreenShot.create! valid_attributes
        # Trigger the behavior that occurs when invalid params are submitted
        ScreenShot.any_instance.stub(:save).and_return(false)
        put :update, :id => screen_shot.id.to_s, :screen_shot => {}
